package com.lloydsbanking.training.java;

public class HelloWorld {

	/**
	 * 
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i = 0; i < 10; i++) {
			if(i == 0)
				System.out.println("Hello World!!! Printed the " + (i+1) + " time");
			else
				System.out.println("Hello World!!! Printed the " + (i+1) + " times");
		}
	}

}
